function onSubmit() {
    //Type appropriate comment here, and begin script below
    var flgSubmit = true;

	//jslog("test message, pls ignore" + g_user.getFullName());

    g_form.clearMessages();
    if (g_form.getValue("state") == "3" && g_form.getValue("comments") == "") {
        g_form.addErrorMessage("Please provide some comments");
		g_form.setMandatory("comments",true);
		g_form.addDecoration("comments","icon-start","VIP");
        flgSubmit = false;
    }

    if (flgSubmit) {
        var flgConfirm = confirm("Thank you for submitting a NeedIt request.Shall I proceed with request submission");
        return flgConfirm;
    } else
        return false;

